public class Solution {
    
    public void drawCircle(int[][] board){
        int r = board.length / 2;
        
        int xCenter = r;
        int yCenter = r;
        
        for(int i = 0; i < board.length; i++){
            for(int j = 0; j < board.length; j++){
                int x = xCenter - i;
                int y = yCenter - j;
                if(Math.round(Math.sqrt(x * x + y * y)) == r){
                    board[i][j] = '*';
                }
            }
        }
    }
}
